import React, {
  useState,
  useCallback,
  useEffect,
  useRef,
  useContext,
  useLayoutEffect,
} from 'react'
import './Section LCR.css'

export const SectionLCR = (props) => {
  // ADAPTIVE V1

  const [layoutWidth, setLayoutWidth] = useState(0)
  const layoutRef = useRef(null)

  const [isResizing, setIsResizing] = useState(false)

  const startResizing = (mouseDownEvent) => {
    setIsResizing(true)
  }

  const stopResizing = () => {
    setIsResizing(false)
  }

  useEffect(() => {
    const handleResize = () => {
      if (layoutRef.current) {
        setLayoutWidth(layoutRef.current.offsetWidth)
      }
    }
    handleResize()
    window.addEventListener('resize', handleResize)
    window.addEventListener('mousemove', handleResize)
    window.addEventListener('mouseup', stopResizing)

    return () => {
      window.removeEventListener('resize', handleResize)
      window.removeEventListener('mousemove', handleResize)
      window.removeEventListener('mouseup', stopResizing)
    }
  }, [])

  console.log(layoutWidth)

  // BRAKE POINTS

  const isLayoutSm = layoutWidth <= parseInt(props.breakPointSM)
  const isLayoutMd =
    layoutWidth > parseInt(props.breakPointSM) &&
    layoutWidth <= parseInt(props.breakPointML)
  const isLayoutLg = layoutWidth > parseInt(props.breakPointML)
  const isLayoutD =
    isLayoutSm === false && isLayoutMd === false && isLayoutLg === false
      ? true
      : false

  const extraControlWidth =
    isLayoutSm === true && props.extraControlWidthS
      ? props.extraControlWidthS
      : isLayoutMd === true && props.extraControlWidthM
      ? props.extraControlWidthM
      : isLayoutLg === true && props.extraControlWidthL
      ? props.extraControlWidthL
      : props.extraControlWidth

  return (
    <section className="section-grid">
      <div className="left-block">{props.leftBlock}</div>
      <div className="central-block">{props.centerBlock}</div>
      <div className="right-block">{props.rightBlock}</div>
    </section>
  )
}
