import React, { useRef, useEffect, useState } from 'react'
import './Section.css'
import { IphoneGraphicBlock } from './IphoneGraphicBlock.jsx'
import { WebGraphicBlock } from './WebGraphicBlock.jsx'
import { PictureGraphicBlock } from './PictureGraphicBlock.jsx'
import { SectionContext } from './SectionProvider'

export const Section = (props) => {
  const windowWidth = window.innerWidth
  const textWidth = 754
  // const dsvdfv = window.innerWidth -
  // создать реф для дива секции
  //const sectionRef = useRef(null)
  //расстояние сверху экрана до верхней границы дива
  //const sectionTop = () => sectionRef.current.getBoundingClientRect().top
  //расстояние сверху экрана до нижней границы дива
  //const sectionBottom = () => sectionRef.current.getBoundingClientRect().top
  //const sectionCurrentHeight = () => sectionRef.current.getBoundingClientRect()
  //const eddev = sectionCurrentHeight()
  //const evefvfrv = eddev + sectionCurrentHeight
  //console.log(`evefvfrv ${sectionCurrentHeight}`)
  //значение активного уровня
  //const dvsdv = 40
  //условие пересечения секции и активного уровня если входит то активная.
  //const evdfvdfv = dvsdv < sectionTop() ? 'Yes' : 'No'
  //console.log(evdfvdfv)

  //
  const divRef = useRef(null)
  const lineY = 100 // Выберите нужное значение для горизонтальной линии

  const [isIntersecting, setIsIntersecting] = useState(false)

  useEffect(() => {
    const checkIntersection = () => {
      if (divRef.current) {
        const divRect = divRef.current.getBoundingClientRect()
        const divTop = divRect.top
        const divBottom = divRect.bottom

        if (divTop < lineY && divBottom > lineY) {
          setIsIntersecting(true)
        } else {
          setIsIntersecting(false)
        }
      }
    }

    window.addEventListener('scroll', checkIntersection)

    // Вызываем функцию проверки в начале, чтобы установить начальное состояние
    checkIntersection()

    return () => {
      window.removeEventListener('scroll', checkIntersection)
    }
  }, [])
  //

  const iPhoneGraphicBlockLeftMargin =
    (windowWidth - textWidth - 288 - 288) / 2 - 30
  console.log(iPhoneGraphicBlockLeftMargin)

  const iPhoneGraphicBlockRightMargin =
    windowWidth - ((windowWidth - textWidth) / 2 - 30)
  console.log(iPhoneGraphicBlockLeftMargin)

  const leftGraphicBlock =
    isIntersecting == true && props.leftGraphicBlock == 'iphone' ? (
      <IphoneGraphicBlock
        left={iPhoneGraphicBlockLeftMargin}
        image={props.leftIcon}
      />
    ) : isIntersecting == true && props.leftGraphicBlock == 'web' ? (
      <WebGraphicBlock image={props.leftIcon} />
    ) : isIntersecting == true && props.leftGraphicBlock == 'picture' ? (
      <PictureGraphicBlock image={props.leftIcon} />
    ) : null

  const rightGraphicBlock =
    isIntersecting == true && props.leftGraphicBlock == 'iphone' ? (
      <IphoneGraphicBlock
        left={iPhoneGraphicBlockRightMargin}
        image={props.leftIcon}
      />
    ) : isIntersecting == true && props.leftGraphicBlock == 'web' ? (
      <WebGraphicBlock image={props.leftIcon} />
    ) : isIntersecting == true && props.leftGraphicBlock == 'picture' ? (
      <PictureGraphicBlock image={props.leftIcon} />
    ) : null

  const textBlock =
    props.textBlockType == 'h1' ? (
      <h1>{props.text}</h1>
    ) : props.textBlockType == 'h2' ? (
      <h2>{props.text}</h2>
    ) : props.textBlockType == 'h3' ? (
      <h3>{props.text}</h3>
    ) : props.textBlockType == 'h4' ? (
      <h4>{props.text}</h4>
    ) : props.textBlockType == 'h5' ? (
      <h5>{props.text}</h5>
    ) : props.textBlockType == 'h6' ? (
      <h6>{props.text}</h6>
    ) : props.textBlockType == 'p' ? (
      <p>{props.text}</p>
    ) : props.textBlockType == 'c' ? (
      <p className="caption">{props.text}</p>
    ) : null
  const textBlockStyle = {
    width: props.textWidth,
  }

  const leftRightBlockWidth = (windowWidth - textWidth) / 2
  const leftBlockStyle = {
    width: leftRightBlockWidth,
  }

  const rightBlockStyle = {
    width: leftRightBlockWidth,
    marginLeft: leftRightBlockWidth + textWidth,
  }

  const [leftBlock, setLeftBlock] = useState(null)
  const [rightBlock, setRightBlock] = useState(null)
  return (
    <section ref={divRef}>
      <SectionContext.Provider
        value={[leftBlock, setLeftBlock, rightBlock, setRightBlock]}
      >
        <div style={leftBlockStyle} className="left-graphic-block">
          {leftGraphicBlock}
        </div>
        {/* <div className="left-indicator-block"></div> */}
        <div style={textBlockStyle} className="central-block">
          {props.children}
        </div>
        {/* <div className="right-indicator-block"></div> */}
        <div style={rightBlockStyle} className="right-graphic-block">
          {rightGraphicBlock}
        </div>
      </SectionContext.Provider>
    </section>
  )
}
