      <h2>{props.text}</h2>
    ) : props.textBlockType == 'h3' ? (
      <h3>{props.text}</h3>
    ) : props.textBlockType == 'h4' ? (
      <h4>{props.text}</h4>
    ) : props.textBlockType == 'h5' ? (
      <h5>{props.text}</h5>
    ) : props.textBlockType == 'h6' ? (
      <h6>{props.text}</h6>
    ) : props.textBlockType == 'p' ? (
      <p>{props.text}</p>
    ) : props.textBlockType == 'c' ? (
      <p className="caption">{props.text}</p>
    ) : null
  const textBlockStyle = {
    width: props.textWidth,
  }

  const leftRightBlockWidth = (windowWidth - textWidth) / 2
  const leftBlockStyle = {
